import React from 'react';

// JSX - javascript XML
// importacoes sempre por aqui, manter HTML mais simples possivel 

import './styles/global.css';



// funcion vai receber propriedades 

interface TitleProps{
  text: string;
}


function Title(props:TitleProps){
  return(
    // <h1>Olá NLW3!!! Tudo certo agora!</h1>
  <h1>{props.text}</h1>
  )
}


// toda vez que passa um atributo ( className="App" )para um componente
// chama isso de propriedade

function App() {
  return (
    <div className="App">
      <Title text="Título 1" />
      <Title text="Título 2" />
      <Title text="Título 3" />
      <Title text="Título 4" />
    </div>
  );
}

export default App;
